function Toolbar()
{
    const [pipelineComponents, setPipelineComponents] = useState([]);

    const onAddBtnClick = event => {
        setPipelineComponents(pipelineComponents.concat(<div> text </div>));
    };
    var len = data.componentData.length ; 
    var rows = Math.ceil(len / 4);
    var remainingElements = len;
    var iter = 1;
    return(

        <div>
            <div id="toolbar" class="our-services section">
                <div class="container">
                <div class="row">
                    <div class="col-lg-6 offset-lg-3">
                    <div class="section-heading wow bounceIn" data-wow-duration="1s" data-wow-delay="0.2s">
                        <h2>CV <span>Toolbar</span></h2>
                    </div>
                    </div>
                </div>
                </div>
            </div>
            <div id="features" class="features section">
              <div class="container">
                 <div class="row">
                 <div className="row justify-content-center">
                    {data.componentData.map((item, index) => {
                        return(

                            <div class="col-lg-3">
                             <div class="features-item first-feature wow fadeInUp" data-wow-duration="1s" data-wow-delay="0s" onClick={onAddBtnClick}>
                             <div class="first-number number">
                                <h6>{iter++}</h6>
                               </div>
                               <div class="icon"></div>
                               <h4>{item.title}</h4>
                               <div class="line-dec"></div>
                               <p>{item.desc}</p>
                             </div>
                          </div>
                        )
                    })}

                 </div>
                 <div class="col-lg-12">
                        <div class="main-green-button scroll-to-section View-Pipeline">
                            <a href="#yourpipeline">View Pipeline</a>
                        </div>
                </div> 
                 </div>
                </div>
            </div>  

        </div>
    );
}

export default Toolbar;



